mapper.update:
update orders


	amount=#{amount} ,


	status=#{status}


where recipient_id=#{recipientId}

select.where:
o.seller_id = s.seller_id and o.product_id = p.product_id and o.buyer_id = b.buyer_id and o.recipient_id = r.recipient_id 

and o.status = #{status}

order by o.recipient_id desc

mapper.select:
select oa,ob,oc,od,oe,og,oh,oi, pa,pb,pc,pd,pe,pf,pg,ph,pi, ba,bb,bc,bd,be,bf, sa,sb,sc,sd,se,sf,sg,sh,si,sj, ra,rb,rc,rd,re,rf from ( select ceil(rownum/#{contentsPerPage}) page, oid oa, opid ob, obid oc, oa od, os oe, orid og, osid oh, ord oi, pid pa ,pn pb, ppp pc, pc pd ,ppi pe, pip pf, prd pg, ped ph, psid pi, bbid ba, bp bb , bn bc, ba bd, bn be, bph bf, ssid sa, sp sb, sn sc, sg sd, sa se, se sf, sph sg, sa sh, sad si, scn sj, rrid ra, rn rb, ra rc, rph rd, rd re, rp rf
from ( select o.order_id oid , o.product_id opid, o.buyer_id obid, o.amount oa, o.status os, o.recipient_id orid, o.seller_id osid, o.regi_date ord, p.product_id pid,p.product_name pn, p.product_price ppp, p.category pc, p.product_info ppi,p.image_path pip, p.regi_date prd,p.exp_date ped, p.seller_id psid, b.buyer_id bbid, b.password bp, b.name bn, b.address ba, b.email be, b.phone bph, s.seller_id ssid, s.password sp, s.name sn, s.gender sg, s.address sa, s.email se, s.phone sph, s.auth sau, s.admin sad, s.corporate_number scn, r.recipient_id rrid, r.name rn, r.address ra, r.phone rph, r.detail rd,r.postcode rp from orders o,product p, buyer b, recipient r,seller s where o.seller_id = s.seller_id and o.product_id = p.product_id and o.buyer_id = b.buyer_id and o.recipient_id = r.recipient_id
and o.status = #{status}
order by o.recipient_id desc ) ) where page = #{page}

where o.seller_id = s.seller_id and o.product_id = p.product_id and o.buyer_id = b.buyer_id and o.recipient_id = r.recipient_id and o.seller_id = #{sellerId}

 and status = #{status}

order by o.recipient_id desc
